package StringLab001;

public class StringMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// TO UpperCase
		String S= "Welcome to selenium Course";
	    S=S.toUpperCase();
	    System.out.println(S);
	    
         // TOLowercase
	    String S1= "Welcome to selenium Course";
	    S1=S1.toLowerCase();
	    System.out.println(S1);

	}

}
